import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function TaskForm({ task, isEdit }) {
  const [formData, setFormData] = useState({
    title: task?.title || '',
    description: task?.description || '',
    dueDate: task?.dueDate ? new Date(task.dueDate).toISOString().split('T')[0] : '',
    dueTime: task?.dueDate ? new Date(task.dueDate).toISOString().slice(11, 16) : '',
    priority: task?.priority || 'Low',
  });
  const [titleError, setTitleError] = useState('');
  const [formError, setFormError] = useState('');
  const navigate = useNavigate();

  // Get today's date for min attribute
  const today = new Date().toISOString().split('T')[0];

  // Validate title on change
  useEffect(() => {
    if (formData.title.length > 0 && formData.title.length < 3) {
      setTitleError('Title must be at least 3 characters long');
    } else {
      setTitleError('');
    }
  }, [formData.title]);

  // Auto-dismiss form error after 3 seconds
  useEffect(() => {
    if (formError) {
      const timer = setTimeout(() => {
        setFormError('');
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [formError]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.title.length < 3) {
      setTitleError('Title must be at least 3 characters long');
      return;
    }
    if (!formData.dueDate) {
      setFormError('Due date is required');
      return;
    }
    try {
      // Combine dueDate and dueTime into a single ISO date string
      const combinedDueDate = formData.dueDate && formData.dueTime
        ? new Date(`${formData.dueDate}T${formData.dueTime}:00`).toISOString()
        : formData.dueDate
        ? new Date(`${formData.dueDate}T00:00:00`).toISOString()
        : null;

      const payload = {
        title: formData.title,
        description: formData.description,
        dueDate: combinedDueDate,
        priority: formData.priority,
      };

      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const url = isEdit
        ? `${process.env.REACT_APP_API_URL}/api/tasks/${task._id}`
        : `${process.env.REACT_APP_API_URL}/api/tasks`;
      const method = isEdit ? 'put' : 'post';
      await axios[method](url, payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
      navigate('/', { state: { successMessage: isEdit ? 'Task successfully updated!' : 'Task successfully added!' } });
    } catch (error) {
      console.error('Error saving task:', error);
      setFormError(error.response?.data?.message || 'Failed to save task. Please try again.');
    }
  };

  // Determine if submit button should be disabled
  const isSubmitDisabled = !formData.title || formData.title.length < 3 || !formData.dueDate;

  return (
    <div className="container mx-auto p-6 pt-20">
      <div className="bg-black bg-opacity-60 p-8 rounded-xl shadow-xl border border-gray-700 hover:border-blue-500 transition-all duration-300 max-w-lg mx-auto">
        <h2 className="text-2xl font-extrabold mb-6 text-center bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
          {isEdit ? 'Edit Task' : 'Add Task'}
        </h2>
        {formError && (
          <div className="mb-4 p-3 bg-red-600 bg-opacity-20 rounded-lg text-red-400 text-sm animate-fade-in flex items-center justify-between">
            <span>{formError}</span>
            <button onClick={() => setFormError('')} className="p-1 hover:bg-red-700 rounded-full transition-colors duration-300">
              <svg
                className="w-4 h-4"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        )}
        <div className="space-y-6">
          <div className="relative">
            <label className="block text-gray-300 text-sm font-medium mb-1 transition-all duration-300">
              Title
            </label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={`w-full p-3 bg-gray-800 border ${titleError ? 'border-red-500' : 'border-gray-700'} rounded-lg text-white focus:outline-none focus:ring-2 ${titleError ? 'focus:ring-red-500' : 'focus:ring-blue-500'} focus:border-transparent transition-all duration-300 hover:bg-gray-700`}
              required
              placeholder="Enter task title"
            />
            {titleError && (
              <p className="mt-1 text-red-400 text-xs animate-fade-in">{titleError}</p>
            )}
          </div>
          <div className="relative">
            <label className="block text-gray-300 text-sm font-medium mb-1 transition-all duration-300">
              Description
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300 hover:bg-gray-700"
              rows="4"
              placeholder="Enter task description (optional)"
            />
          </div>
          <div className="relative">
            <label className="block text-gray-300 text-sm font-medium mb-1 transition-all duration-300">
              Due Date
            </label>
            <input
              type="date"
              name="dueDate"
              value={formData.dueDate}
              onChange={handleChange}
              min={today}
              className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300 hover:bg-gray-700"
              required
            />
          </div>
          <div className="relative">
            <label className="block text-gray-300 text-sm font-medium mb-1 transition-all duration-300">
              Due Time
            </label>
            <input
              type="time"
              name="dueTime"
              value={formData.dueTime}
              onChange={handleChange}
              className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300 hover:bg-gray-700"
            />
          </div>
          <div className="relative">
            <label className="block text-gray-300 text-sm font-medium mb-1 transition-all duration-300">
              Priority
            </label>
            <select
              name="priority"
              value={formData.priority}
              onChange={handleChange}
              className="w-full p-3 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300 hover:bg-gray-700 appearance-none"
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
            <svg
              className="absolute right-3 top-10 w-5 h-5 text-gray-400 pointer-events-none"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M19 9l-7 7-7-7"
              />
            </svg>
          </div>
          <button
            onClick={handleSubmit}
            disabled={isSubmitDisabled}
            className={`flex items-center justify-center space-x-2 w-full px-4 py-3 rounded-full text-white font-semibold transition-all duration-300 ${
              isSubmitDisabled
                ? 'bg-gray-600 cursor-not-allowed'
                : 'bg-gradient-to-r from-blue-600 to-blue-800 hover:from-blue-500 hover:to-blue-700 hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] hover:scale-105'
            }`}
          >
            <svg
              className="w-5 h-5"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d={isEdit ? 'M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15.828H9v-2.828l8.586-8.586z' : 'M12 4v16m8-8H4'}
              />
            </svg>
            <span>{isEdit ? 'Update Task' : 'Add Task'}</span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default TaskForm;
