import { Link } from 'react-router-dom';
import axios from 'axios';

function TaskCard({ task, onDelete, onToggleStatus }) {
  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${task._id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      onDelete(task._id);
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleToggleStatus = async () => {
    try {
      await axios.put(`http://localhost:5000/api/tasks/${task._id}`, {
        status: task.status === 'Pending' ? 'Completed' : 'Pending',
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      onToggleStatus(task._id);
    } catch (error) {
      console.error('Error toggling status:', error);
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow-md mb-4">
      <h3 className="text-lg font-bold">{task.title}</h3>
      <p>{task.description}</p>
      <p>Due: {new Date(task.dueDate).toLocaleDateString()}</p>
      <p>Priority: {task.priority}</p>
      <p>Status: {task.status}</p>
      <div className="mt-2">
        <button
          onClick={handleToggleStatus}
          className={`mr-2 px-4 py-2 rounded ${task.status === 'Pending' ? 'bg-green-500' : 'bg-yellow-500'} text-white`}
        >
          {task.status === 'Pending' ? 'Mark Completed' : 'Mark Pending'}
        </button>
        <Link to={`/edit-task/${task._id}`} className="mr-2 px-4 py-2 bg-blue-500 text-white rounded">Edit</Link>
        <button onClick={handleDelete} className="px-4 py-2 bg-red-500 text-white rounded">Delete</button>
      </div>
    </div>
  );
}

export default TaskCard;
