import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Avatar from 'boring-avatars';

function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [user, setUser] = useState({ email: '', name: '' });
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [nameInput, setNameInput] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch user data on mount
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;
        const response = await axios.get('http://localhost:5000/api/user', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser({ email: response.data.email || '', name: response.data.name || '' });
        setNameInput(response.data.name || '');
      } catch (error) {
        console.error('Error fetching user data:', error);
        setErrorMessage('Failed to load user data');
      }
    };
    fetchUser();
  }, []);

  // Auto-dismiss messages after 3 seconds
  useEffect(() => {
    if (successMessage || errorMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage('');
        setErrorMessage('');
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage, errorMessage]);

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Handle name save
  const handleSaveName = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }
      await axios.put(
        'http://localhost:5000/api/user',
        { name: nameInput },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(prev => ({ ...prev, name: nameInput }));
      setSuccessMessage('Name saved successfully!');
      setDropdownOpen(false);
    } catch (error) {
      console.error('Error saving name:', error);
      setErrorMessage('Failed to save name');
    }
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-gray-900 p-4 shadow-lg border-b border-gray-700 z-50">
      <div className="container mx-auto flex justify-between items-center">
        <Link
          to="/"
          className="text-2xl font-extrabold bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent hover:text-blue-400 transition-colors duration-300"
        >
          TaskMaster
        </Link>
        <div className="flex space-x-4 items-center">
          {token ? (
            <>
              <Link
                to="/add-task"
                className="flex items-center space-x-2 px-5 py-2.5 rounded-full bg-gradient-to-r from-blue-600 to-blue-800 text-white font-semibold hover:from-blue-500 hover:to-blue-700 hover:scale-105 hover:shadow-[0_0_10px_rgba(59,130,246,0.5)] transition-all duration-300"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M12 4v16m8-8H4"
                  />
                </svg>
                <span>Add Task</span>
              </Link>
              <div className="relative">
                <button
                  onClick={() => setDropdownOpen(!dropdownOpen)}
                  className="flex items-center space-x-2"
                >
                  <Avatar
                    size={40}
                    name={user.email || 'default'}
                    variant="marble"
                    colors={['#3b82f6', '#a855f7', '#22c55e', '#f43f5e', '#eab308']}
                  />
                  {user.name && (
                    <span className="text-gray-300 text-base font-semibold">
                      Hi, {user.name}!
                    </span>
                  )}
                </button>
                {dropdownOpen && (
                  <div className="absolute top-full right-0 mt-2 w-64 bg-gray-800 border border-gray-700 rounded-lg shadow-lg p-4 z-50 animate-fade-in transition-all duration-300">
                    {successMessage && (
                      <p className="text-green-400 text-sm mb-2">{successMessage}</p>
                    )}
                    {errorMessage && (
                      <p className="text-red-400 text-sm mb-2">{errorMessage}</p>
                    )}
                    <p className="text-gray-300 text-base mb-2">Email: {user.email}</p>
                    <input
                      type="text"
                      value={nameInput}
                      onChange={(e) => setNameInput(e.target.value)}
                      placeholder="Enter your name"
                      className="w-full p-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-500 mb-2"
                    />
                    <button
                      onClick={handleSaveName}
                      className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-300"
                    >
                      Save
                    </button>
                  </div>
                )}
              </div>
              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 px-5 py-2.5 rounded-full bg-gradient-to-r from-red-600 to-red-800 text-white font-semibold hover:from-red-500 hover:to-red-700 hover:scale-105 hover:shadow-[0_0_10px_rgba(239,68,68,0.5)] transition-all duration-300"
              >
                <svg
                  className="w-5 h-5"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1"
                  />
                </svg>
                <span>Logout</span>
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="text-gray-300 px-5 py-2.5 rounded-full hover:bg-blue-600 hover:text-white transition-colors duration-300"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="text-gray-300 px-5 py-2.5 rounded-full hover:bg-blue-600 hover:text-white transition-colors duration-300"
              >
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
